sys_script_include.script:
function validateEmailAddress(emailStr){
		// JavaScriptの強制力を使ってemailStrが文字列であることを保証する
		emailStr = emailStr + '';
		// emailStrを正規表現で指定された許容構文と比較する
		// emailStrが許可された構文であればtrueを、そうでなければfalseを返します。
		if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
			return true;
		}
		else {
		 return false;
		}
	}
